import { createStore } from 'vuex'
import { map } from '../Ymaps/ymaps.js'
import axios from 'axios'

export default createStore({
  state: {
    placesArr: [],
    marksConfigs: [
      {
        id: 'universities', 
        config: {
          iconLayout: 'default#image',
          iconImageHref: '/placeMarks/purpleMark.svg',
          iconImageSize: [55, 55],
          iconImageOffset: [-30, -40],  
        }
      },
      {
        id: 'coworkings', 
        config: {
          iconLayout: 'default#image',
          iconImageHref: '/placeMarks/yellowMark.svg',
          iconImageSize: [55, 55],
          iconImageOffset: [-30, -40],  
        }
      },
      {
        id: 'libraries', 
        config: {
          iconLayout: 'default#image',
          iconImageHref: '/placeMarks/yellowMark.svg',
          iconImageSize: [55, 55],
          iconImageOffset: [-30, -40],  
        }
      },
      {
        id: 'cafe', 
        config: {
          iconLayout: 'default#image',
          iconImageHref: '/placeMarks/yellowMark.svg',
          iconImageSize: [55, 55],
          iconImageOffset: [-30, -40],  
        }
      },
    ]
  },
  mutations: {
  },
  actions: {
    async addMarksFromType({ state, commit }, btnId) {
        if (!map) {
          return
        };

        try {
          const response = await axios.get(`http://194.87.248.192:8000/point/category/${btnId}`)

          let newCollection = new ymaps.GeoObjectCollection(null);
          state.placesArr = response.data;
          let config = state.marksConfigs.find(item => item.id === btnId).config;

          for(let place of state.placesArr) {
            newCollection.add(new ymaps.Placemark(place.coords, {
              balloonContentHeader: place.name,
              balloonContentBody: place.addess,
            }, config))
          };

          map.geoObjects.add(newCollection);

        } catch (e) {
          alert("Ошибка")
        }

