import { createStore } from 'vuex'
const ymaps = window.ymaps;

export default createStore({
   state: {
    map: null,
  },
  mutations: {
    SET_MAP(state, map) {
      state.map = map
    },
  },
  actions: {
    createMap({ commit }) {
      ymaps.ready(() => {
        const map = new ymaps.Map('map', {
          center: [55.76, 37.64],
          zoom: 10,
        })
        commit('SET_MAP', map)
      })
    },
  },
})
