import { createStore } from 'vuex'

export default createStore({
  state: {
    map: null,
    marks: [] // массив для хранения меток
  },
  mutations: {
    SET_MAP(state, map) {
      state.map = map
    },
    ADD_MARK(state, mark) {
      state.marks.push(mark)
    }
  },
  actions: {
    createMap({ commit }) {
      ymaps.ready(() => {
        const map = new ymaps.Map('map', {
          center: [59.938955, 30.315644],
          zoom: 10,
          controls: [],
        })
        commit('SET_MAP', map)
      })
    },
    addMark({ state, commit }, { coordinates, balloonContent }) {
      if (!state.map) {
        console.log(state.map)
        return;
      }

      const mark = new ymaps.Placemark(coordinates, {
        balloonContent
      });

      state.map.geoObjects.add(mark);
      commit('ADD_MARK', mark);
    }
  }
})
