import { createStore } from 'vuex'

export default createStore({
  state: {
    map: null,
    marksConfigs: [

    ]
  },
  mutations: {
    SET_MAP(state, map) {
      state.map = map
    },
  },
  actions: {
    createMap({ commit }) {
      ymaps.ready(() => {
        const map = new ymaps.Map('map', {
          center: [59.938955, 30.315644],
          zoom: 10,
          controls: [],
        })
        commit('SET_MAP', map)
      })
    },
  },
})
